~/wp gatsby-markdown-export \
  --directory=/home/typeofweb/wp-markdown/ \
  --skip_copy_uploads \
  --skip_original_images \
  --create_type_directory \
  --post_types=post,page,tag,category,serie \
  --include_private_fields=_yoast_wpseo_focuskeywords,_yoast_wpseo_focuskw_text_input,_yoast_wpseo_focuskw,_yoast_wpseo_is_cornerstone,_yoast_wpseo_keywordsynonyms,_yoast_wpseo_meta-robots-nofollow,_yoast_wpseo_metadesc,_yoast_wpseo_opengraph-image-id,_yoast_wpseo_opengraph-image,_yoast_wpseo_title,_yoast_wpseo_twitter-image \
  --excluded_front_matter=yst_prominent_words,onesignal_meta_box_present,onesignal_send_notification,wp_featherlight_disable \
  --remap_fields='_yoast_wpseo_focuskeywords,seo_focuskeywords;_yoast_wpseo_focuskw_text_input,seo_focuskw_text_input;_yoast_wpseo_focuskw,seo_focuskw;_yoast_wpseo_is_cornerstone,seo_is_cornerstone;_yoast_wpseo_keywordsynonyms,seo_keywordsynonyms;_yoast_wpseo_meta-robots-nofollow,seo_meta-robots-nofollow;_yoast_wpseo_metadesc,seo_metadesc;_yoast_wpseo_opengraph-image-id,seo_opengraph-image-id;_yoast_wpseo_opengraph-image,seo_opengraph-image;_yoast_wpseo_title,seo_title;_yoast_wpseo_twitter-image,seo_twitter-image'
